ter_frases where idNews = "'.$_GET['news'].'" order by id desc');						while($dados = mysql_fetch_assoc($sql)){			?>			<tr>				<td><?php echo $dados['frase']?></td>							<td><a href="<?php echo 'plugins/newsmade/ajax.citacoes.php?news='.$_GET['news'].'&amp;del='.$dados['id']?>" class="jfbox"><img src="<?php echo $_ll['tema']['icones'];?>/trash.png" alt="excluir"/></a></td>			</tr>			<?php		}					?>	</table></div>